var BudgetUtilsClient = Class.create();
BudgetUtilsClient.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    calculateTotalBudget: function() {
        var eventId = this.getParameter("event_id");
        return JSON.stringify(new BudgetUtils().calculateTotalBudget(eventId));
    },

    calculateRemainingBudget: function() {
        var eventId = this.getParameter("event_id");
        return JSON.stringify(new BudgetUtils().calculateRemainingBudget(eventId));
    },

    isBudgetApproved: function() {
        var eventId = this.getParameter("event_id");
        return JSON.stringify(new BudgetUtils().isBudgetApproved(eventId));
    },

    getBudgetDetailsByEvent: function() {
        var eventId = this.getParameter("event_id");
        return JSON.stringify(new BudgetUtils().getBudgetDetailsByEvent(eventId));
    },

    type: 'BudgetUtilsClient'
});
